import React, { useEffect, useState } from 'react';
import { LineChart, Line, XAxis, YAxis, Tooltip, CartesianGrid, Legend, ResponsiveContainer } from 'recharts';
import axios from 'axios';

// Colors array for different stocks
const lineColors = ['#ff3333', '#33ff33', '#3333ff', '#ffcc33', '#cc33ff', '#33cccc'];

// Define available periods
const periods = [
  { label: '1 Week', value: 'week' },
  { label: '1 Month', value: 'month' },
  { label: '1 Year', value: 'year' },
  { label: '5 Years', value: '5year' }
];

const StockComparison = () => {
  const [stockData, setStockData] = useState({});
  const [availableStocks, setAvailableStocks] = useState(['AAL', 'AASM', 'AATM', 'ABL', 'ABOT', 'ABSON']); // Example tickers
  const [addedStocks, setAddedStocks] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false); // State to manage modal visibility
  const [selectedStock, setSelectedStock] = useState(''); // State to track selected stock from the modal
  const [selectedPeriod, setSelectedPeriod] = useState('month'); // Default period selection

  // Fetch stock data from API for a specific stock
  const fetchStockData = (ticker, period) => {
    axios.get(`http://localhost:5000/api/stock-data/${ticker}?period=${period}`)
      .then(response => {
        const formattedData = response.data.map(item => ({
          timestamp: new Date(item.timestamp).toLocaleDateString('en-US'),
          open: item.open,
          close: item.close,
        })).sort((a, b) => new Date(a.timestamp) - new Date(b.timestamp));

        setStockData(prevState => ({
          ...prevState,
          [ticker]: formattedData
        }));
      })
      .catch(error => {
        console.error('Error fetching stock data', error);
      });
  };

  // Handle adding stock from the modal
  const handleAddStock = () => {
    if (selectedStock) {
      setAddedStocks([...addedStocks, selectedStock]);
      fetchStockData(selectedStock, selectedPeriod); // Fetch data for the selected period
      setAvailableStocks(availableStocks.filter(stock => stock !== selectedStock));
      setIsModalOpen(false); // Close the modal after adding the stock
    }
  };

  // Open the modal to select a stock
  const openModal = () => {
    setIsModalOpen(true);
  };

  // Close the modal without adding a stock
  const closeModal = () => {
    setIsModalOpen(false);
  };

  // Handle selecting a stock from the dropdown in the modal
  const handleStockSelection = (e) => {
    setSelectedStock(e.target.value);
  };

  // Handle period change
  const handlePeriodChange = (period) => {
    setSelectedPeriod(period);
    // Refetch data for all added stocks with the new period
    addedStocks.forEach(stock => {
      fetchStockData(stock, period);
    });
  };

  return (
    <div>
      <h2>Compare Stocks</h2>

      {/* Toggle buttons to select the period */}
      <div style={{ marginBottom: '20px' }}>
        {periods.map(period => (
          <button
            key={period.value}
            onClick={() => handlePeriodChange(period.value)}
            style={{
              marginRight: '10px',
              padding: '10px 20px',
              backgroundColor: selectedPeriod === period.value ? '#007bff' : '#f0f0f0',
              color: selectedPeriod === period.value ? '#fff' : '#000',
              border: '1px solid #ccc',
              cursor: 'pointer',
            }}
          >
            {period.label}
          </button>
        ))}
      </div>

      {/* Button to open modal to add stocks */}
      <button
        onClick={openModal}
        style={{
          marginBottom: '20px',
          padding: '10px 20px',
          backgroundColor: '#007bff',
          color: '#fff',
          border: 'none',
          cursor: 'pointer',
        }}
      >
        Add Stocks
      </button>

      {/* Modal for selecting stocks */}
      {isModalOpen && (
        <div style={modalStyles.overlay}>
          <div style={modalStyles.modal}>
            <h3>Select a stock to add</h3>
            <select
              value={selectedStock}
              onChange={handleStockSelection}
              style={{ padding: '10px', width: '100%', marginBottom: '20px' }}
            >
              <option value="" disabled>Select a stock</option>
              {availableStocks.map(stock => (
                <option key={stock} value={stock}>
                  {stock}
                </option>
              ))}
            </select>
            <div style={{ textAlign: 'right' }}>
              <button onClick={handleAddStock} style={modalStyles.button}>Add</button>
              <button onClick={closeModal} style={{ ...modalStyles.button, backgroundColor: '#ccc' }}>Cancel</button>
            </div>
          </div>
        </div>
      )}

      {/* Stock comparison chart */}
      <ResponsiveContainer width="100%" height={400}>
        <LineChart margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="timestamp" />
          <YAxis />
          <Tooltip />
          <Legend />

          {/* Dynamically render lines for each added stock */}
          {addedStocks.map((stock, index) => (
            <Line
              key={stock}
              type="monotone"
              data={stockData[stock]}
              dataKey="close"  // You can also compare on 'open' or other properties
              name={stock}
              stroke={lineColors[index % lineColors.length]}  // Assign a unique color
              strokeWidth={3}
              activeDot={{ r: 8 }}
            />
          ))}
        </LineChart>
      </ResponsiveContainer>
    </div>
  );
};

// Simple modal styles (can be customized)
const modalStyles = {
  overlay: {
    position: 'fixed',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    zIndex: 1000,  // Ensure overlay is on top of the page content
  },
  modal: {
    backgroundColor: '#fff',
    padding: '20px',
    borderRadius: '10px',
    width: '400px',
    boxShadow: '0 5px 15px rgba(0, 0, 0, 0.3)',
    zIndex: 1100,  // Ensure modal content is above the overlay
    position: 'relative', // Ensure proper positioning above the overlay
  },
  button: {
    padding: '10px 20px',
    backgroundColor: '#007bff',
    color: '#fff',
    border: 'none',
    cursor: 'pointer',
    marginLeft: '10px',
  }
};

export default StockComparison;
