import React, { useEffect, useState } from 'react';
import { LineChart, Line, XAxis, YAxis, Tooltip, CartesianGrid, Legend, ResponsiveContainer } from 'recharts';
import axios from 'axios';

const StockChart = () => {
  const [stockData, setStockData] = useState([]);
  const [selectedTicker, setSelectedTicker] = useState('AATM'); // Default ticker
  const [tickers, setTickers] = useState(['AAL', 'AASM', 'AATM', 'ABL', 'ABOT', 'ABSON']); // Example tickers

  // Fetch stock data from API based on selected ticker
  const fetchStockData = (ticker) => {
    axios.get(`http://localhost:5000/api/stock-data/${ticker}`)
      .then(response => {
        const formattedData = response.data.map(item => ({
          timestamp: new Date(item.timestamp).toLocaleDateString('en-US'),
          open: item.open,
          close: item.close,
        })).sort((a, b) => new Date(a.timestamp) - new Date(b.timestamp));
        setStockData(formattedData);
      })
      .catch(error => {
        console.error('Error fetching stock data', error);
      });
  };

  useEffect(() => {
    // Fetch data for the default selected ticker when the component mounts
    fetchStockData(selectedTicker);
  }, [selectedTicker]);

  // Handle dropdown change
  const handleTickerChange = (e) => {
    setSelectedTicker(e.target.value);
  };

  return (
    <div>
      <h2>Stock Data for {selectedTicker}</h2>
      
      {/* Dropdown to select ticker */}
      <label htmlFor="tickerSelect">Select Ticker: </label>
      <select id="tickerSelect" value={selectedTicker} onChange={handleTickerChange}>
        {tickers.map(ticker => (
          <option key={ticker} value={ticker}>
            {ticker}
          </option>
        ))}
      </select>

      {/* Stock chart */}
      <ResponsiveContainer width="100%" height={400}>
        <LineChart data={stockData} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="timestamp" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Line type="monotone" dataKey="open" stroke="#1A3636" strokeWidth={3} activeDot={{ r: 8 }} />
          <Line type="monotone" dataKey="close" stroke="#f5dda4" strokeWidth={3} />
        </LineChart>
      </ResponsiveContainer>
    </div>
  );
};

export default StockChart;
