import React, { useEffect, useState } from 'react';
import { LineChart, Line, XAxis, YAxis, Tooltip, CartesianGrid, Legend, ResponsiveContainer } from 'recharts';
import axios from 'axios';

// Colors array for different stocks
const lineColors = ['#ffffff', '#ffc333', '#3333ff', '#2aa34d', '#cc33ff', '#33cccc'];

// Define available periods
const periods = [
  { label: '1 Week', value: '1W' },
  { label: '1 Month', value: '1M' },
  { label: '1 Year', value: '1Y' },
  { label: '5 Years', value: '5Y' }
];

// Filter data based on the selected time period
const filterDataByPeriod = (data, period) => {
  const now = new Date("2024-09-18"); // Use the current date as a reference
  let filteredData = [];

  switch (period) {
    case '1W': // Last 1 week
      filteredData = data.filter(item => (now - new Date(item.timestamp)) <= 7 * 24 * 60 * 60 * 1000);
      break;
    case '1M': // Last 1 month
      filteredData = data.filter(item => (now - new Date(item.timestamp)) <= 30 * 24 * 60 * 60 * 1000);
      break;
    case '1Y': // Last 1 year
      filteredData = data.filter(item => (now - new Date(item.timestamp)) <= 365 * 24 * 60 * 60 * 1000);
      break;
    case '5Y': // Last 5 years
      filteredData = data.filter(item => (now - new Date(item.timestamp)) <= 5 * 365 * 24 * 60 * 60 * 1000);
      break;
    default:
      filteredData = data; // Default to showing all data
      break;
  }

  return filteredData;
};

const StockComparison = () => {
  const [stockData, setStockData] = useState({});
  const [availableStocks, setAvailableStocks] = useState([]);
  const [addedStocks, setAddedStocks] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false); // State to manage modal visibility
  const [selectedStock, setSelectedStock] = useState(''); // State to track selected stock from the modal
  const [selectedPeriod, setSelectedPeriod] = useState('1W'); // Default period selection

  // Fetch ticker names from the backend
  useEffect(() => {
    axios.get('http://localhost:5000/api/tickers')
      .then(response => {
        setAvailableStocks(response.data);
        if (response.data.length > 0) {
          setSelectedStock(response.data[0]); // Set default ticker to the first one
        }
      })
      .catch(error => {
        console.error('Error fetching tickers', error);
      });
  }, []);

  // Fetch stock data from API for a specific stock
  const fetchStockData = (ticker) => {
    axios.get(`http://localhost:5000/api/stock-data/${ticker}`)
      .then(response => {
        const formattedData = response.data.map(item => ({
          timestamp: new Date(item.timestamp).toLocaleDateString('en-US'),
          open: item.open,
          close: item.close,
        })).sort((a, b) => new Date(a.timestamp) - new Date(b.timestamp));

        setStockData(prevState => ({
          ...prevState,
          [ticker]: formattedData
        }));
      })
      .catch(error => {
        console.error('Error fetching stock data', error);
      });
  };

  // Handle adding stock from the modal
  const handleAddStock = () => {
    if (selectedStock) {
      setAddedStocks([...addedStocks, selectedStock]);
      fetchStockData(selectedStock); // Fetch full data for the stock
      setAvailableStocks(availableStocks.filter(stock => stock !== selectedStock));
      setIsModalOpen(false); // Close the modal after adding the stock
    }
  };

  // Open the modal to select a stock
  const openModal = () => {
    setIsModalOpen(true);
  };

  // Close the modal without adding a stock
  const closeModal = () => {
    setIsModalOpen(false);
  };

  // Handle selecting a stock from the dropdown in the modal
  const handleStockSelection = (e) => {
    setSelectedStock(e.target.value);
  };

  // Handle period change
  const handlePeriodChange = (period) => {
    setSelectedPeriod(period);
  };

  return (
    <div>
      <h2>Compare Stocks</h2>

      {/* Toggle buttons to select the period */}
      {/* Time period toggle buttons */}
      <div style={{ margin: '20px 0' }}>
        <button
          onClick={() => handlePeriodChange('1W')}
          style={{
            marginRight: '10px',
            padding: '10px 20px',
            backgroundColor: selectedPeriod === '1W' ? '#ffc333' : '#161616',
            cursor: 'pointer',
          }}
        >
          1 Week
        </button>
        <button
          onClick={() => handlePeriodChange('1M')}
          style={{
            marginRight: '10px',
            padding: '10px 20px',
            backgroundColor: selectedPeriod === '1M' ? '#ffc333' : '#161616',
            cursor: 'pointer',
          }}
        >
          1 Month
        </button>
        <button
          onClick={() => handlePeriodChange('1Y')}
          style={{
            marginRight: '10px',
            padding: '10px 20px',
            backgroundColor: selectedPeriod === '1Y' ? '#ffc333' : '#161616',
            cursor: 'pointer',
          }}
        >
          1 Year
        </button>
        <button
          onClick={() => handlePeriodChange('5Y')}
          style={{
            padding: '10px 20px',
            backgroundColor: selectedPeriod === '5Y' ? '#ffc333' : '#161616',
            cursor: 'pointer',
          }}
        >
          5 Years
        </button>
      </div>


      {/* Button to open modal to add stocks */}
      <button
        onClick={openModal}
        style={{
          marginBottom: '20px',
          padding: '10px 20px',
          backgroundColor: '#161616',
          color: '#fff',
          border: 'none',
          cursor: 'pointer',
        }}
      >
        Add Stocks
      </button>

      {/* Modal for selecting stocks */}
      {isModalOpen && (
        <div style={modalStyles.overlay}>
          <div style={modalStyles.modal}>
            <h3>Select a stock to add</h3>
            <select
              value={selectedStock}
              onChange={handleStockSelection}
              style={{ padding: '10px', width: '100%', marginBottom: '20px' }}
            >
              <option value="" disabled>Select a stock</option>
              {availableStocks.map(stock => (
                <option key={stock} value={stock}>
                  {stock}
                </option>
              ))}
            </select>
            <div style={{ textAlign: 'right' }}>
              <button onClick={handleAddStock} style={{ ...modalStyles.button, backgroundColor: '#161616' }}>Add</button>
              <button onClick={closeModal} style={{ ...modalStyles.button, backgroundColor: '#ffdddd' }}>Cancel</button>
            </div>
          </div>
        </div>
      )}

      {/* Stock comparison chart */}
      <ResponsiveContainer width="100%" height={400}>
        <LineChart margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="timestamp" />
          <YAxis />
          <Tooltip />
          <Legend />

          {/* Dynamically render lines for each added stock */}
          {addedStocks.map((stock, index) => {
            const filteredStockData = filterDataByPeriod(stockData[stock] || [], selectedPeriod); // Filter data by selected period
            return (
              <Line
                key={stock}
                type="monotone"
                data={filteredStockData}
                dataKey="close"  // You can also compare on 'open' or other properties
                name={stock}
                stroke={lineColors[index % lineColors.length]}  // Assign a unique color
                strokeWidth={3}
                activeDot={{ r: 8 }}
              />
            );
          })}
        </LineChart>
      </ResponsiveContainer>
    </div>
  );
};

// Simple modal styles (can be customized)
const modalStyles = {
  overlay: {
    position: 'fixed',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    zIndex: 1000,  // Ensure overlay is on top of the page content
  },
  modal: {
    backgroundColor: '#1f1f1f',
    padding: '20px',
    borderRadius: '10px',
    width: '400px',
    boxShadow: '0 5px 15px rgba(0, 0, 0, 0.3)',
    zIndex: 1100,  // Ensure modal content is above the overlay
    position: 'relative', // Ensure proper positioning above the overlay
  },
  button: {
    padding: '10px 20px',
    backgroundColor: '#007bff',
    color: '#fff',
    border: 'none',
    cursor: 'pointer',
    marginLeft: '10px',
  }
};

export default StockComparison;
