// src/App.js
import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import { createTheme, ThemeProvider, CssBaseline, Container } from '@mui/material';
import StockChart from './components/StockChart';
import StockIndicators from './components/StockIndicators';  // New StockIndicators component
import Sidebar from './components/Sidebar';  // Sidebar component for navigation
import PredictedStockPrices from './components/PredictedStockPrices';
import MarketSentiment from './components/MarketSentiment';

const darkTheme = createTheme({
  palette: {
    mode: 'dark',
  },
});

function App() {
  return (
    <ThemeProvider theme={darkTheme}>
      <CssBaseline />
      <Router>
        <div style={{ display: 'flex' }}>
          <Sidebar /> {/* Sidebar for navigation */}
          <Container style={{ marginLeft: '200px' }}> {/* Adjust container based on sidebar */}
            <Routes>
              <Route path="/" element={<StockChart />} />  {/* Main page for stock chart */}
              <Route path="/indicators" element={<StockIndicators />} />  {/* Second page for stock indicators */}
              <Route path="/predictions" element={<PredictedStockPrices />} /> {/* Third page for stock predictions */}
              <Route path="/sentiment" element={<MarketSentiment />} /> {/* Third page for stock predictions */}
              <Route path="/compare" element={<Compare />} /> {/* Third page for stock predictions */}
            </Routes>
          </Container>
        </div>
      </Router>
    </ThemeProvider>
  );
}

export default App;
